INSERT INTO `jk_shop_goods` VALUES (841, '11047014', '丸美丸美纯色之恋柔光素颜霜', '318.00', 73, 1, '<p><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">丸美丸美纯色之恋柔光素颜霜</span></p><p><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><br/></span></p><p><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">产品规格：50g &nbsp; 产品价格：318元</span></p><p><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><br/></span></p><p><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">主要功效：一抹柔光，美肌焕颜，深层润养，防护外界刺激，亮肤不干燥。自然隐匿毛孔细纹，配合洁面产品即可轻松清洗，无需卸妆。</span></p><p><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><br/></span></p><p><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">适合肤质：适用于需要隐匿细纹与毛孔、追求裸妆效果同时能滋养肌肤等人群。</span></p><p><br/></p>', 1505201313, '', NULL, NULL, 10, NULL, NULL, NULL, NULL);
