INSERT INTO `jk_shop_goods` VALUES (973, '11059003', '丸美弹力蛋白眼精华素3g+3g（迷你装）', '￥99', 73, 1, '', 1578470371, 'http://p.marubi.cn/backend/Uploads/Video/20200107/5e13e757703f6.mp4', '/backend/Uploads/Shop/goods/20200107/5e13e7443a5eb.JPG', '3g+3g', 1, '3g+3g', '<p>3分钟瞬间紧致，24小时持续淡纹</p><p>丸美弹力蛋白，来自挪威北极纯净深海金枪鱼，韧性更高、更丰富、更纯净；特别添加密蒙花提取物，360度防御光衰老。</p>', '', '<p>1. 专利技术：国家权威专利认证，丸美抗衰配方Marubi Elastin Plus ™ ，专利号201610835181.9</p><p>2. 四无添加：无酒精、无色素、无香精、无矿物油</p><p>3. 功效认证：法国里昂医科大学实验室权威测试，45天，弹力蛋白增加119%</p><p>4. 奖项：2018年度化妆品品类卓越奖，2018嘉人百大赏一呼百应奖最佳眼部保养，2018美容风云榜-至臻经典大奖，瑞丽美容大赏特别大奖，2018海报美妆大赏-年度Q弹逆龄眼部精华奖</p><p><br/></p>');
